import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [studentName, setStudentName] = useState('');
  const [certificateHash, setCertificateHash] = useState('');
  const [verificationResult, setVerificationResult] = useState('');

  // üìå Belge Ekleme Fonksiyonu
  const addCertificate = async () => {
    try {
      await axios.post(`${process.env.REACT_APP_BACKEND_URL}/add-certificate`, {
        studentName,
        certificateHash,
      });
      alert('‚úÖ Belge ba≈üarƒ±yla blokzincire eklendi!');
    } catch (error) {
      console.error(error);
      alert('‚ùå Belge eklenirken hata olu≈ütu!');
    }
  };

  // üìå Belge Doƒürulama Fonksiyonu
  const verifyCertificate = async () => {
    try {
      const response = await axios.get(
        `${process.env.REACT_APP_BACKEND_URL}/verify-certificate/${certificateHash}`
      );
      setVerificationResult(
        `üéì √ñƒürenci Adƒ±: ${response.data.studentName}, üìÖ Zaman Damgasƒ±: ${new Date(
          response.data.timestamp * 1000
        ).toLocaleString()}`
      );
    } catch (error) {
      console.error(error);
      alert('‚ùå Belge doƒürulama sƒ±rasƒ±nda hata olu≈ütu!');
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial, sans-serif' }}>
      <h1>üéì Mezuniyet Belgesi Sistemi</h1>

      {/* Belge Ekle */}
      <div>
        <h3>üì• Belge Ekle</h3>
        <input
          type="text"
          placeholder="√ñƒürenci Adƒ±"
          value={studentName}
          onChange={(e) => setStudentName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Belge Hash"
          value={certificateHash}
          onChange={(e) => setCertificateHash(e.target.value)}
        />
        <button onClick={addCertificate}>Belge Ekle</button>
      </div>

      {/* Belge Doƒürula */}
      <div style={{ marginTop: '2rem' }}>
        <h3>üîç Belge Doƒürula</h3>
        <input
          type="text"
          placeholder="Belge Hash"
          value={certificateHash}
          onChange={(e) => setCertificateHash(e.target.value)}
        />
        <button onClick={verifyCertificate}>Belge Doƒürula</button>
        {verificationResult && <p>{verificationResult}</p>}
      </div>
    </div>
  );
}

export default App;
