import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function Transactions() {
  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_BACKEND_URL}/get-all-transactions`
        );
        setTransactions(response.data.transactions);
        setError('');
      } catch (error) {
        console.error('Blockchain işlemleri hatası:', error);
        setError('❌ Blockchain işlemleri alınırken bir hata oluştu.');
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []);

  return (
    <div className="container">
      <h1>🔗 Blockchain Tüm İşlemleri</h1>
      {loading && <p>⏳ İşlemler yükleniyor...</p>}
      {error && <div className="error">{error}</div>}

      {!loading && transactions.length === 0 && (
        <p>📭 Henüz herhangi bir işlem bulunamadı.</p>
      )}

      {!loading && transactions.length > 0 && (
        <div className="transaction-table">
          <table>
            <thead>
              <tr>
                <th>Hash</th>
                <th>Gönderen</th>
                <th>Alıcı</th>
                <th>Miktar (ETH)</th>
                <th>Zaman</th>
                <th>Blok No</th>
                <th>Öğrenci Adı</th>
                <th>Belge Hash</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((tx, index) => (
                <tr key={index}>
                  <td>{tx.hash.slice(0, 10)}...</td>
                  <td>{tx.from || 'N/A'}</td>
                  <td>{tx.to || 'N/A'}</td>
                  <td>{tx.value}</td>
                  <td>{new Date(tx.timestamp).toLocaleString()}</td>
                  <td>{tx.blockNumber}</td>
                  <td>{tx.studentName || 'Bilinmiyor'}</td>
                  <td>{tx.certificateHash || 'Bilinmiyor'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Transactions;
