import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Transactions from './Transactions';

function App() {
  const [studentName, setStudentName] = useState('');
  const [certificateHash, setCertificateHash] = useState('');
  const [verificationResult, setVerificationResult] = useState('');
  const [error, setError] = useState('');
  const [showTransactions, setShowTransactions] = useState(false);
  const [pdfFile, setPdfFile] = useState(null);
  const [pdfs, setPdfs] = useState([]);

  // 📌 Belge Ekleme Fonksiyonu
  const addCertificate = async () => {
    try {
      if (!studentName || !certificateHash) {
        throw new Error('Öğrenci adı ve belge hash alanları doldurulmalıdır.');
      }

      await axios.post(`${process.env.REACT_APP_BACKEND_URL}/add-certificate`, {
        studentName,
        certificateHash,
      });

      setError('');
      alert('✅ Belge başarıyla blokzincire eklendi!');
    } catch (error) {
      setError(`❌ ${error.response?.data?.error || error.message}`);
    }
  };

  // 📌 PDF Yükleme
  const uploadPDF = async () => {
    try {
      if (!pdfFile || !studentName || !certificateHash) {
        throw new Error('Öğrenci adı, belge hash ve PDF dosyası zorunludur.');
      }

      const formData = new FormData();
      formData.append('pdf', pdfFile);
      formData.append('studentName', studentName);
      formData.append('certificateHash', certificateHash);

      await axios.post(`${process.env.REACT_APP_BACKEND_URL}/upload-pdf`, formData);

      alert('✅ PDF dosyası başarıyla yüklendi!');
      fetchPdfs();
    } catch (error) {
      setError(`❌ ${error.response?.data?.error || error.message}`);
    }
  };

  // 📌 PDF Listesi
  const fetchPdfs = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/list-pdfs`);
      setPdfs(response.data.pdfs);
    } catch (error) {
      setError('❌ PDF listesi alınırken hata oluştu.');
    }
  };

  useEffect(() => {
    fetchPdfs();
  }, []);

  return (
    <div className="container">
      <h1>🎓 Mezuniyet Belgesi Sistemi</h1>

      <button onClick={() => setShowTransactions(!showTransactions)}>
        {showTransactions ? '🏠 Ana Sayfa' : '🔗 Blockchain İşlemlerini Gör'}
      </button>

      {showTransactions ? (
        <Transactions />
      ) : (
        <>
          {/* Belge Ekle */}
          <h3>📥 Belge Ekle</h3>
          <input placeholder="Öğrenci Adı" onChange={(e) => setStudentName(e.target.value)} />
          <input placeholder="Belge Hash" onChange={(e) => setCertificateHash(e.target.value)} />
          <button onClick={addCertificate}>Belge Ekle</button>

          {/* PDF Yükle */}
          <h3>📤 PDF Yükle</h3>
          <input type="file" accept="application/pdf" onChange={(e) => setPdfFile(e.target.files[0])} />
          <button onClick={uploadPDF}>PDF Yükle</button>

          {/* PDF Listesi */}
          <h3>📑 PDF Listesi</h3>
          <ul>
            {pdfs.map((pdf) => (
              <li key={pdf._id}>
                {pdf.studentName} - {pdf.fileName} 
                <button onClick={() => window.open(`${process.env.REACT_APP_BACKEND_URL}/view-pdf/${pdf._id}`)}>
                  Görüntüle
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
