import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import Transactions from './Transactions'; // Blockchain i≈ülemleri sayfasƒ±

function App() {
  const [studentName, setStudentName] = useState('');
  const [certificateHash, setCertificateHash] = useState('');
  const [verificationResult, setVerificationResult] = useState('');
  const [error, setError] = useState('');
  const [showTransactions, setShowTransactions] = useState(false); // Sayfa kontrol√º i√ßin state
  const [pdfFile, setPdfFile] = useState(null); // PDF dosyasƒ± i√ßin state
  const [pdfViewId, setPdfViewId] = useState(''); // G√∂r√ºnt√ºlenecek PDF ID

  // üìå Belge Ekleme Fonksiyonu
  const addCertificate = async () => {
    try {
      if (!studentName || !certificateHash) {
        throw new Error('√ñƒürenci adƒ± ve belge hash alanlarƒ± doldurulmalƒ±dƒ±r.');
      }

      await axios.post(`${process.env.REACT_APP_BACKEND_URL}/add-certificate`, {
        studentName,
        certificateHash,
      });

      setError('');
      alert('‚úÖ Belge ba≈üarƒ±yla blokzincire eklendi!');
    } catch (error) {
      console.error('Belge ekleme hatasƒ±:', error);

      if (error.response) {
        setError(`‚ùå Hata: ${error.response.data.error}`);
      } else if (error.request) {
        setError('‚ùå Sunucudan cevap alƒ±namadƒ±.');
      } else {
        setError(`‚ùå Hata: ${error.message}`);
      }
    }
  };

  // üìå Belge Doƒürulama Fonksiyonu
  const verifyCertificate = async () => {
    try {
      if (!certificateHash) {
        throw new Error('Belge hash alanƒ± doldurulmalƒ±dƒ±r.');
      }

      const response = await axios.get(
        `${process.env.REACT_APP_BACKEND_URL}/verify-certificate/${certificateHash}`
      );

      const date = new Date(response.data.timestamp);
      const formattedDate = date.toLocaleString();

      setVerificationResult(
        `üéì √ñƒürenci Adƒ±: ${response.data.studentName}, üìÖ Zaman Damgasƒ±: ${formattedDate}`
      );
      setError('');
    } catch (error) {
      console.error('Belge doƒürulama hatasƒ±:', error);

      if (error.response) {
        setError(`‚ùå Hata: ${error.response.data.error}`);
      } else if (error.request) {
        setError('‚ùå Sunucudan cevap alƒ±namadƒ±.');
      } else {
        setError(`‚ùå Hata: ${error.message}`);
      }
    }
  };

  // üìå PDF Y√ºkleme Fonksiyonu
  const uploadPDF = async () => {
    try {
      if (!pdfFile || !studentName || !certificateHash) {
        throw new Error('√ñƒürenci adƒ±, belge hash ve PDF dosyasƒ± zorunludur.');
      }

      const formData = new FormData();
      formData.append('pdf', pdfFile);
      formData.append('studentName', studentName);
      formData.append('certificateHash', certificateHash);

      const response = await axios.post(
        `${process.env.REACT_APP_BACKEND_URL}/upload-pdf`,
        formData
      );

      alert('‚úÖ PDF dosyasƒ± ba≈üarƒ±yla y√ºklendi!');
      setPdfViewId(response.data.fileId);
    } catch (error) {
      console.error('PDF y√ºkleme hatasƒ±:', error);
      setError('‚ùå PDF y√ºklenirken bir hata olu≈ütu.');
    }
  };

  // üìå PDF G√∂r√ºnt√ºleme Fonksiyonu
  const viewPDF = () => {
    if (!pdfViewId) {
      alert('‚ùó √ñnce bir PDF dosyasƒ± y√ºklemelisiniz.');
      return;
    }

    window.open(`${process.env.REACT_APP_BACKEND_URL}/view-pdf/${pdfViewId}`, '_blank');
  };

  return (
    <div className="container">
      <h1>üéì Mezuniyet Belgesi Sistemi</h1>

      <button onClick={() => setShowTransactions(!showTransactions)}>
        {showTransactions ? 'üè† Ana Sayfa' : 'üîó Blockchain ƒ∞≈ülemlerini G√∂r'}
      </button>

      {/* Sayfa Y√∂nlendirmesi */}
      {showTransactions ? (
        <Transactions />
      ) : (
        <>
          {/* Belge Ekle */}
          <h3>üì• Belge Ekle</h3>
          <input
            type="text"
            placeholder="√ñƒürenci Adƒ±"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Belge Hash"
            value={certificateHash}
            onChange={(e) => setCertificateHash(e.target.value)}
          />
          <button onClick={addCertificate}>Belge Ekle</button>

          {/* Belge Doƒürula */}
          <h3>üîç Belge Doƒürula</h3>
          <input
            type="text"
            placeholder="Belge Hash"
            value={certificateHash}
            onChange={(e) => setCertificateHash(e.target.value)}
          />
          <button onClick={verifyCertificate}>Belge Doƒürula</button>

          {/* Doƒürulama Sonucu */}
          {verificationResult && <div className="result">{verificationResult}</div>}

          {/* PDF Y√ºkleme */}
          <h3>üì§ PDF Y√ºkle</h3>
          <input
            type="file"
            accept="application/pdf"
            onChange={(e) => setPdfFile(e.target.files[0])}
          />
          <button onClick={uploadPDF}>PDF Y√ºkle</button>

          {/* PDF G√∂r√ºnt√ºleme */}
          <h3>üìë PDF G√∂r√ºnt√ºle</h3>
          <button onClick={viewPDF}>PDF Dosyasƒ±nƒ± G√∂r√ºnt√ºle</button>

          {/* Hata Mesajlarƒ± */}
          {error && <div className="error">{error}</div>}
        </>
      )}

      <footer>
        ¬© {new Date().getFullYear()} Mezuniyet Belgesi Sistemi
      </footer>
    </div>
  );
}

export default App;
