import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Transactions from './Transactions'; // Blockchain i≈ülemleri sayfasƒ±

function App() {
  // üìå Blockchain State'leri
  const [studentName, setStudentName] = useState('');
  const [certificateHash, setCertificateHash] = useState('');
  const [verificationResult, setVerificationResult] = useState('');
  const [error, setError] = useState('');
  const [showTransactions, setShowTransactions] = useState(false);

  // üìå PDF State'leri
  const [pdfHashId, setPdfHashId] = useState(''); // PDF i√ßin benzersiz ID
  const [pdfFile, setPdfFile] = useState(null); // PDF dosya nesnesi
  const [pdfs, setPdfs] = useState([]); // PDF listesi
  const [pdfError, setPdfError] = useState(''); // PDF hata mesajƒ±

  // üìå Blockchain: Belge Ekleme
  const addCertificate = async () => {
    try {
      if (!studentName || !certificateHash) {
        throw new Error('√ñƒürenci adƒ± ve belge hash alanlarƒ± doldurulmalƒ±dƒ±r.');
      }

      await axios.post(`${process.env.REACT_APP_BACKEND_URL}/add-certificate`, {
        studentName,
        certificateHash,
      });

      setError('');
      alert('‚úÖ Belge ba≈üarƒ±yla blokzincire eklendi!');
    } catch (error) {
      console.error('Belge ekleme hatasƒ±:', error);
      setError(`‚ùå ${error.response?.data?.error || error.message}`);
    }
  };

  // üìå Blockchain: Belge Doƒürulama
  const verifyCertificate = async () => {
    try {
      if (!certificateHash) {
        throw new Error('Belge hash alanƒ± doldurulmalƒ±dƒ±r.');
      }

      const response = await axios.get(
        `${process.env.REACT_APP_BACKEND_URL}/verify-certificate/${certificateHash}`
      );

      const date = new Date(response.data.timestamp);
      const formattedDate = date.toLocaleString();

      setVerificationResult(
        `üéì √ñƒürenci Adƒ±: ${response.data.studentName}, üìÖ Zaman Damgasƒ±: ${formattedDate}`
      );
      setError('');
    } catch (error) {
      console.error('Belge doƒürulama hatasƒ±:', error);
      setError(`‚ùå ${error.response?.data?.error || error.message}`);
    }
  };

  // üìå Blockchain: T√ºm ƒ∞≈ülemleri Getirme
  const fetchTransactions = async () => {
    try {
      const response = await axios.get(
        `${process.env.REACT_APP_BACKEND_URL}/get-all-transactions`
      );
      console.log('Blockchain ƒ∞≈ülemleri:', response.data.transactions);
    } catch (error) {
      console.error('Blockchain i≈ülemleri hatasƒ±:', error);
      setError('‚ùå Blockchain i≈ülemleri alƒ±nƒ±rken hata olu≈ütu.');
    }
  };

  // üìå PDF: Dosya Y√ºkleme
  const uploadPDF = async () => {
    try {
      if (!pdfHashId || !pdfFile) {
        throw new Error('Hash ID ve PDF dosyasƒ± zorunludur.');
      }

      const formData = new FormData();
      formData.append('pdf', pdfFile);
      formData.append('hashId', pdfHashId);

      await axios.post(`${process.env.REACT_APP_BACKEND_URL}/upload-pdf`, formData);

      alert('‚úÖ PDF dosyasƒ± ba≈üarƒ±yla y√ºklendi!');
      fetchPdfs(); // PDF listesi g√ºncelle
    } catch (error) {
      console.error('PDF y√ºkleme hatasƒ±:', error);
      setPdfError(`‚ùå ${error.response?.data?.error || error.message}`);
    }
  };

  // üìå PDF: Listeleme
  const fetchPdfs = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/list-pdfs`);
      setPdfs(response.data.pdfs);
    } catch (error) {
      console.error('PDF listeleme hatasƒ±:', error);
      setPdfError('‚ùå PDF listesi alƒ±nƒ±rken hata olu≈ütu.');
    }
  };

  // üìå PDF: G√∂r√ºnt√ºleme
  const viewPDF = (id) => {
    window.open(`${process.env.REACT_APP_BACKEND_URL}/view-pdf/${id}`, '_blank');
  };

  useEffect(() => {
    fetchTransactions();
    fetchPdfs();
  }, []);

  return (
    <div className="container">
      <h1>üéì Mezuniyet Belgesi Sistemi</h1>

      {/* Sayfa Y√∂nlendirme */}
      <button onClick={() => setShowTransactions(!showTransactions)}>
        {showTransactions ? 'üè† Ana Sayfa' : 'üîó Blockchain ƒ∞≈ülemlerini G√∂r'}
      </button>

      {showTransactions ? (
        <Transactions />
      ) : (
        <>
          {/* Blockchain ƒ∞≈ülemleri */}
          <h3>üì• Belge Ekle</h3>
          <input
            type="text"
            placeholder="√ñƒürenci Adƒ±"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Belge Hash"
            value={certificateHash}
            onChange={(e) => setCertificateHash(e.target.value)}
          />
          <button onClick={addCertificate}>Belge Ekle</button>

          <h3>üîç Belge Doƒürula</h3>
          <input
            type="text"
            placeholder="Belge Hash"
            value={certificateHash}
            onChange={(e) => setCertificateHash(e.target.value)}
          />
          <button onClick={verifyCertificate}>Belge Doƒürula</button>
          {verificationResult && <div>{verificationResult}</div>}

          {/* PDF ƒ∞≈ülemleri */}
          <h3>üì§ PDF Y√ºkle</h3>
          <input
            type="text"
            placeholder="Hash ID"
            value={pdfHashId}
            onChange={(e) => setPdfHashId(e.target.value)}
          />
          <input
            type="file"
            accept="application/pdf"
            onChange={(e) => setPdfFile(e.target.files[0])}
          />
          <button onClick={uploadPDF}>PDF Y√ºkle</button>

          <h3>üìë PDF Listesi</h3>
          <ul>
            {pdfs.map((pdf) => (
              <li key={pdf._id}>
                {pdf.fileName} - {pdf.hashId}
                <button onClick={() => viewPDF(pdf._id)}>G√∂r√ºnt√ºle</button>
              </li>
            ))}
          </ul>
        </>
      )}

      {/* Hata Mesajlarƒ± */}
      {error && <div className="error">{error}</div>}
      {pdfError && <div className="error">{pdfError}</div>}

      <footer>
        ¬© {new Date().getFullYear()} Mezuniyet Belgesi Sistemi
      </footer>
    </div>
  );
}

export default App;
